-- Crear la tabla restaurantes
CREATE TABLE restaurantes (
  id_restaurante INTEGER PRIMARY KEY AUTOINCREMENT,
  tipo_restaurante VARCHAR(255),
  subtipo_restaurante VARCHAR(255),
  nombre_restaurante VARCHAR(255),
  url_restaurante VARCHAR(512)
);

